INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B1_I2', 'ingles-b1-U37', 2, 'multiple_choice', 'Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La forma correcta es ''have been waiting'' porque empezaste a esperar en el pasado y todavía estás esperando.","stimulus_en":"I ___ (wait) for you since 5 PM.","options":[{"id":"o1","text":"have been waiting"},{"id":"o2","text":"waited"},{"id":"o3","text":"was waiting"},{"id":"o4","text":"am waiting"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
