INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B1_I3', 'ingles-b1-U37', 3, 'multiple_choice', 'Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La respuesta correcta es ''has been working'' porque habla de una acción que sigue en progreso desde hace dos años.","stimulus_en":"She ___ (work) on her thesis for two years.","options":[{"id":"o1","text":"has worked"},{"id":"o2","text":"have worked"},{"id":"o3","text":"has been working"},{"id":"o4","text":"is working"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B1_I4', 'ingles-b1-U37', 4, 'multiple_choice', 'Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.', '{"prompt_es":"Choose the correct answer to complete each sentence using the Present Perfect Continuous tense.","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"La opción correcta es ''have been playing'', ya que se refiere a una actividad que comenzó en el pasado y está en curso.","stimulus_en":"We ___ (play) football for an hour.","options":[{"id":"o1","text":"have been playing"},{"id":"o2","text":"have played"},{"id":"o3","text":"are playing"},{"id":"o4","text":"played"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
