INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B2_I1', 'ingles-b1-U37', 6, 'fill_blanks', 'Fill in the gaps with the correct form of the word provided.

Base words: (1) CHANGE, (2) ACCESS, (3) COMMUNICATE, (4) PORTABLE, (5) ACCESS', '{"prompt_es":"Fill in the gaps with the correct form of the word provided.\n\nBase words: (1) CHANGE, (2) ACCESS, (3) COMMUNICATE, (4) PORTABLE, (5) ACCESS","mastery_tag":"vocabulary:b1-technology","main_text":"Technology has made a big (1)___ in our everyday lives. The (2)___ of the internet has changed how we communicate. Many people use social media for (3)___ purposes. The rise of (4)___ devices has also improved our (5)___ to information.","correct_answer":["CHANGE","ACCESS","COMMUNICATION","PORTABLE","ACCESS"],"explanation":"(1) La palabra ''change'' se convierte en ''change'' en este contexto como sustantivo.\n(2) Aquí, ''access'' se usa como sustantivo y representa la disponibilidad de la internet.\n(3) La forma correcta es ''communication'', que es el sustantivo de ''communicate''.\n(4) La palabra ''portable'' describe dispositivos que son fáciles de transportar.\n(5) Aquí, ''access'' se usa como sustantivo que representa el acto de acceder a la información.","complexity":2,"concept_tags":["vocabulary","technology","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B3_I1', 'ingles-b1-U37', 7, 'multiple_choice', 'What is the main function of a CPU?', '{"prompt_es":"What is the main function of a CPU?","mastery_tag":"vocabulary:b1-technology","explanation":"La CPU (Unidad Central de Procesamiento) es responsable de procesar la información en una computadora.","stimulus_en":"","options":[{"id":"o1","text":"Store data"},{"id":"o2","text":"Process information"},{"id":"o3","text":"Display graphics"},{"id":"o4","text":"Connect devices"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B3_I2', 'ingles-b1-U37', 8, 'multiple_choice', 'Which of the following is a social media platform?', '{"prompt_es":"Which of the following is a social media platform?","mastery_tag":"vocabulary:b1-technology","explanation":"Instagram es una plataforma de redes sociales donde los usuarios pueden compartir fotos y videos.","stimulus_en":"","options":[{"id":"o1","text":"Google"},{"id":"o2","text":"Instagram"},{"id":"o3","text":"Microsoft"},{"id":"o4","text":"Amazon"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
