INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U37_B3_I5', 'ingles-b1-U37', 11, 'multiple_choice', 'Which term describes an online journal?', '{"prompt_es":"Which term describes an online journal?","mastery_tag":"vocabulary:b1-technology","explanation":"Un blog es un tipo de sitio web donde se publican artículos o entradas de diario en formato cronológico.","stimulus_en":"","options":[{"id":"o1","text":"Website"},{"id":"o2","text":"Blog"},{"id":"o3","text":"Forum"},{"id":"o4","text":"App"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","technology","unit","grammar"]}')
