INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B1_I1', 'ingles-b1-U39', 1, 'multiple_choice', 'Choose the correct option to complete the sentences using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete the sentences using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"En este caso, se utiliza el pasado continuo para indicar una acción en progreso que fue interrumpida por otra acción.","stimulus_en":"I (1)___ my homework when the phone rang.","options":[{"id":"o1","text":"was doing"},{"id":"o2","text":"did"},{"id":"o3","text":"do"},{"id":"o4","text":"doing"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B1_I2', 'ingles-b1-U39', 2, 'multiple_choice', 'Choose the correct option to complete the sentences using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete the sentences using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Aquí se usa el pasado continuo porque la acción de ver la película estaba en progreso en el momento en que llegamos.","stimulus_en":"They (2)___ a movie when we arrived.","options":[{"id":"o1","text":"were watching"},{"id":"o2","text":"watched"},{"id":"o3","text":"was watching"},{"id":"o4","text":"watching"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B1_I3', 'ingles-b1-U39', 3, 'multiple_choice', 'What (3)___ you (4)___ at 9 PM yesterday?', '{"prompt_es":"What (3)___ you (4)___ at 9 PM yesterday?","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa ''were'' aquí porque ''you'' en plural se combina con ''were'' en pasado continuo.","stimulus_en":"","options":[{"id":"o1","text":"was"},{"id":"o2","text":"were"},{"id":"o3","text":"did"},{"id":"o4","text":"doing"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B1_I4', 'ingles-b1-U39', 4, 'multiple_choice', 'Choose the correct option to complete the sentences using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete the sentences using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se utiliza el pasado continuo porque la acción de caminar estaba ocurriendo cuando comenzó a llover.","stimulus_en":"She (5)___ outside when it started to rain.","options":[{"id":"o1","text":"was walking"},{"id":"o2","text":"walked"},{"id":"o3","text":"was walk"},{"id":"o4","text":"walking"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B1_I5', 'ingles-b1-U39', 5, 'multiple_choice', 'Choose the correct option to complete the sentences using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete the sentences using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"La acción de tener la cena estaba en progreso cuando ocurrió otro evento (las luces apagándose).","stimulus_en":"While we (6)___ dinner, the lights went out.","options":[{"id":"o1","text":"were having"},{"id":"o2","text":"had"},{"id":"o3","text":"having"},{"id":"o4","text":"was having"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B2_I1', 'ingles-b1-U39', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''who'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''who'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Se debe utilizar ''who'' para referirse a personas en oraciones relativas.","stimulus_en":"The manager is a person. He makes important decisions.\n\nThe manager is a person who... ...","correct_answer":"makes important decisions.","example":"who","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B2_I2', 'ingles-b1-U39', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''which'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''which'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utiliza ''which'' para referirse a cosas y proporcionar información adicional.","stimulus_en":"I visited the museum. It has ancient artifacts.\n\nI visited the museum which... ...","correct_answer":"has ancient artifacts.","example":"which","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B2_I3', 'ingles-b1-U39', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''when'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''when'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utiliza ''when'' para especificar un tiempo particular en oraciones relativas.","stimulus_en":"The conference is on Friday. It starts at 10 AM.\n\nThe conference is on Friday when... ...","correct_answer":"it starts at 10 AM.","example":"when","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B2_I4', 'ingles-b1-U39', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''that'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''that'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Puedes usar ''that'' para unir dos oraciones y referirte a cosas muy específicas.","stimulus_en":"The book is about history. I borrowed it from the library.\n\nThe book that... ...","correct_answer":"I borrowed from the library.","example":"that","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
