INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U39_B2_I5', 'ingles-b1-U39', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''where'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''where'':","mastery_tag":"grammar:b1-relative-clauses","explanation":"Usa ''where'' para referirse a un lugar en oraciones relativas.","stimulus_en":"The place is a restaurant. We had dinner there.\n\nThe place where... ...","correct_answer":"we had dinner.","example":"where","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
