VALUES ('B1_U40_B1_I1', 'ingles-b1-U40', 1, 'multiple_choice', 'What is the main purpose of a story''s introduction?', '{"prompt_es":"What is the main purpose of a story''s introduction?","mastery_tag":"writing:b1-stories","explanation":"La introducción de una historia establece el escenario y presenta a los personajes principales.","stimulus_en":"","options":[{"id":"o1","text":"To create suspense"},{"id":"o2","text":"To introduce characters"},{"id":"o3","text":"To conclude the story"},{"id":"o4","text":"To provide details"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
