VALUES ('B1_U40_B1_I2', 'ingles-b1-U40', 2, 'multiple_choice', 'What should typically happen in the climax of a story?', '{"prompt_es":"What should typically happen in the climax of a story?","mastery_tag":"writing:b1-stories","explanation":"El clímax es el momento de mayor tensión donde se resuelve el problema principal de la historia.","stimulus_en":"","options":[{"id":"o1","text":"The story ends"},{"id":"o2","text":"A problem is resolved"},{"id":"o3","text":"New characters appear"},{"id":"o4","text":"The setting changes"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
