VALUES ('B1_U40_B1_I5', 'ingles-b1-U40', 5, 'multiple_choice', 'Why is it important to use a sequence of events in a story?', '{"prompt_es":"Why is it important to use a sequence of events in a story?","mastery_tag":"writing:b1-stories","explanation":"Una secuencia de eventos ayuda a construir una línea de tiempo que hace que la historia sea coherente.","stimulus_en":"","options":[{"id":"o1","text":"To confuse the reader"},{"id":"o2","text":"To build a timeline"},{"id":"o3","text":"To skip details"},{"id":"o4","text":"To change the characters"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","stories","unit","grammar"]}')
