INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B2_I1', 'ingles-b1-U40', 6, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se utiliza el pasado continuo para describir acciones que estaban en progreso en un momento específico del pasado.","stimulus_en":"During the conference, several important events were happening.\n\nWhile the speakers ___ (present) their ideas, the audience was taking notes.","correct_answer":"were presenting","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B2_I2', 'ingles-b1-U40', 7, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo se utiliza para mostrar que una acción estaba en progreso cuando ocurrió otra acción.","stimulus_en":"During the conference, several important events were happening.\n\nI ___ (work) on my presentation when the fire alarm went off.","correct_answer":"was working","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B2_I3', 'ingles-b1-U40', 8, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"Utilizamos el pasado continuo para indicar una acción que estaba en progreso en un momento determinado en el pasado.","stimulus_en":"During the conference, several important events were happening.\n\nThey ___ (discuss) the project when I arrived.","correct_answer":"were discussing","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B2_I4', 'ingles-b1-U40', 9, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"El pasado continuo se usa para mostrar que tenía lugar una acción, mientras que otra acción ocurrió.","stimulus_en":"During the conference, several important events were happening.\n\nWhile I ___ (check) my emails, my boss called me.","correct_answer":"was checking","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B2_I5', 'ingles-b1-U40', 10, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se emplea el pasado continuo para expresar una acción que estaba en progreso cuando otra acción la interrumpió.","stimulus_en":"During the conference, several important events were happening.\n\nWe ___ (wait) for the results when the announcement happened.","correct_answer":"were waiting","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U40_B3_I1', 'ingles-b1-U40', 11, 'fill_blanks', 'Complete the sentences using the present perfect continuous form.', '{"prompt_es":"Complete the sentences using the present perfect continuous form.","mastery_tag":"grammar:b1-present-perfect-continuous","main_text":"She ___ (work) on this project for three months.\n\nThey ___ (study) English since last year.\n\nI ___ (try) to reach you for the last hour.\n\nWe ___ (discuss) our strategy for a couple of weeks.\n\nHe ___ (work) overtime lately.","correct_answer":["has been working","have been studying","have been trying","have been discussing","has been working"],"explanation":"(1) La forma presente perfecto continuo se usa para acciones que empezaron en el pasado y continúan hasta ahora. En este caso, ''has been working'' indica que el trabajo comenzó hace tres meses y todavía está en progreso.\n(2) Usamos ''have been studying'' para indicar que la acción de estudiar comenzó el año pasado y aún continúa en este momento.\n(3) El presente perfecto continuo se utiliza aquí para mostrar que la acción de intentar comunicarse comenzó en la última hora y todavía está en curso.\n(4) En este caso, estamos utilizando ''have been discussing'' para expresar una conversación que comenzó hace un par de semanas y sigue hasta ahora.\n(5) La frase ''has been working'' indica que la acción de trabajar horas extra comenzó recientemente y sigue ocurriendo.","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

INSERT INTO public.course_lessons (id, module_id, order_index, title, duration)
