VALUES ('B1_U41_B3_I2', 'ingles-b1-U41', 8, 'multiple_choice', 'Choose the correct relative pronoun to complete each sentence.', '{"prompt_es":"Choose the correct relative pronoun to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"Utilizamos ''who'' porque nos referimos a una persona (la artista).","stimulus_en":"She is the artist ___ painted the mural.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"whose"},{"id":"o3","text":"that"},{"id":"o4","text":"which"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
