VALUES ('B1_U41_B3_I4', 'ingles-b1-U41', 10, 'multiple_choice', 'Choose the correct relative pronoun to complete each sentence.', '{"prompt_es":"Choose the correct relative pronoun to complete each sentence.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''That'' se usa aquí para referirse al objeto (la película) en la oración.","stimulus_en":"This is the movie ___ I told you about.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"where"},{"id":"o3","text":"which"},{"id":"o4","text":"that"}],"correct_answer":"o4","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
