INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B1_I1', 'ingles-b1-U42', 1, 'multiple_choice', 'What is the best way to start an informal letter?', '{"prompt_es":"What is the best way to start an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"En una carta informal, ''Hello,'' es una forma amigable y común de empezar.","stimulus_en":"","options":[{"id":"o1","text":"Dear friend,"},{"id":"o2","text":"To whom it may concern,"},{"id":"o3","text":"Hello,"},{"id":"o4","text":"Greetings,"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
