INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B1_I2', 'ingles-b1-U42', 2, 'multiple_choice', 'Which phrase is appropriate for asking about a friend''s family?', '{"prompt_es":"Which phrase is appropriate for asking about a friend''s family?","mastery_tag":"writing:b1-informal-letters","explanation":"Esta pregunta es adecuada para preguntar sobre la familia en una carta informal.","stimulus_en":"","options":[{"id":"o1","text":"How''s the weather?"},{"id":"o2","text":"How is your family?"},{"id":"o3","text":"What time is it?"},{"id":"o4","text":"Where are you going?"}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B1_I3', 'ingles-b1-U42', 3, 'multiple_choice', 'What should you include in the closing of an informal letter?', '{"prompt_es":"What should you include in the closing of an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"‘Best wishes,’ es una forma amigable y común de cerrar una carta informal.","stimulus_en":"","options":[{"id":"o1","text":"Sincerely,"},{"id":"o2","text":"Yours faithfully,"},{"id":"o3","text":"Best wishes,"},{"id":"o4","text":"To whom it may concern,"}],"correct_answer":"o3","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B1_I4', 'ingles-b1-U42', 4, 'multiple_choice', 'What is a good way to ask about a recent trip?', '{"prompt_es":"What is a good way to ask about a recent trip?","mastery_tag":"writing:b1-informal-letters","explanation":"Es una buena pregunta para saber más sobre la experiencia de un amigo.","stimulus_en":"","options":[{"id":"o1","text":"Did you enjoy your trip?"},{"id":"o2","text":"Where are you now?"},{"id":"o3","text":"What is your job?"},{"id":"o4","text":"How old are you?"}],"correct_answer":"o1","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
