INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B1_I5', 'ingles-b1-U42', 5, 'multiple_choice', 'How can you express excitement in an informal letter?', '{"prompt_es":"How can you express excitement in an informal letter?","mastery_tag":"writing:b1-informal-letters","explanation":"‘I am thrilled!’ muestra una emoción fuerte y es adecuada para cartas informales.","stimulus_en":"","options":[{"id":"o1","text":"I am very pleased."},{"id":"o2","text":"I am thrilled!"},{"id":"o3","text":"It is good."},{"id":"o4","text":"I like this."}],"correct_answer":"o2","complexity":2,"concept_tags":["writing","informal","letters","unit","grammar"]}')
