INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U42_B2_I1', 'ingles-b1-U42', 6, 'multiple_choice', 'What is the main purpose of the interview?', '{"prompt_es":"What is the main purpose of the interview?","mastery_tag":"listening:b1-interviews","explanation":"El objetivo principal de una entrevista laboral es conseguir un empleo.","stimulus_en":"","options":[{"id":"o1","text":"To sell a product"},{"id":"o2","text":"To get a job"},{"id":"o3","text":"To make friends"},{"id":"o4","text":"To ask about hobbies"}],"correct_answer":"o2","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
