VALUES ('B1_U42_B2_I2', 'ingles-b1-U42', 7, 'multiple_choice', 'How should you dress for an interview?', '{"prompt_es":"How should you dress for an interview?","mastery_tag":"listening:b1-interviews","explanation":"Vestirse formalmente muestra profesionalismo en una entrevista.","stimulus_en":"","options":[{"id":"o1","text":"Casually"},{"id":"o2","text":"Informally"},{"id":"o3","text":"Formally"},{"id":"o4","text":"Colorfully"}],"correct_answer":"o3","complexity":2,"concept_tags":["listening","interviews","unit","grammar"]}')
