INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B2_I1', 'ingles-b1-U43', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''tomorrow'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''tomorrow'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En el discurso indirecto, ''tomorrow'' se convierte en ''the next day''.","stimulus_en":"She said, ''I will finish the project tomorrow.''\n\nShe said that she would finish the project... ...","correct_answer":"the next day.","example":"tomorrow","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B2_I2', 'ingles-b1-U43', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''job'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''job'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En este caso, se mantiene la misma palabra, pero se cambia el tiempo verbal.","stimulus_en":"He told me, ''You are doing a great job.''\n\nHe told me that I was doing a great... ...","correct_answer":"job.","example":"job","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B2_I3', 'ingles-b1-U43', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''meeting'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''meeting'':","mastery_tag":"grammar:b1-reported-speech","explanation":"Al preguntar, cambia la estructura a una pregunta indirecta.","stimulus_en":"She asked, ''Are you coming to the meeting?''\n\nShe asked if I was coming to the... ...","correct_answer":"meeting.","example":"meeting","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B2_I4', 'ingles-b1-U43', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''conference'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''conference'':","mastery_tag":"grammar:b1-reported-speech","explanation":"El uso de ''can'' cambia a ''could'' al reportar en pasado.","stimulus_en":"He said, ''I can''t attend the conference.''\n\nHe said that he couldn''t attend the... ...","correct_answer":"conference.","example":"conference","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B2_I5', 'ingles-b1-U43', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''report'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''report'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En este caso, el uso de ''please'' se omite en el discurso indirecto.","stimulus_en":"She told him, ''Please send me the report.''\n\nShe told him to send her the... ...","correct_answer":"report.","example":"report","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U43_B3_I1', 'ingles-b1-U43', 11, 'multiple_choice_cloze', 'Fill in the blanks with the correct word from the options provided.', '{"prompt_es":"Fill in the blanks with the correct word from the options provided.","mastery_tag":"vocabulary:b1-education","main_text":"At university, students have to (1)[GAP 1] a lot of information for their exams. They often (2)[GAP 2] in groups to help each other understand the topics. It is important to (3)[GAP 3] the lectures and take good notes. Many students also (4)[GAP 4] outside resources to support their learning. After the exam, they will (5)[GAP 5] their results and see how well they did.","gaps":[{"id":"1","options":[{"id":"o1","text":"study"},{"id":"o2","text":"teach"},{"id":"o3","text":"learn"},{"id":"o4","text":"practice"}]},{"id":"2","options":[{"id":"o1","text":"work"},{"id":"o2","text":"play"},{"id":"o3","text":"talk"},{"id":"o4","text":"meet"}]},{"id":"3","options":[{"id":"o1","text":"attend"},{"id":"o2","text":"ignore"},{"id":"o3","text":"forget"},{"id":"o4","text":"review"}]},{"id":"4","options":[{"id":"o1","text":"take"},{"id":"o2","text":"seek"},{"id":"o3","text":"give"},{"id":"o4","text":"find"}]},{"id":"5","options":[{"id":"o1","text":"discuss"},{"id":"o2","text":"post"},{"id":"o3","text":"check"},{"id":"o4","text":"celebrate"}]}],"correct_answer":{"1":"o1","2":"o4","3":"o1","4":"o2","5":"o1"},"explanation":"(1) El término ''study'' se utiliza para referirse al acto de aprender o repasar información, especialmente en un contexto académico.\n(2) En este contexto, ''meet'' se refiere a reunirse con otros estudiantes para colaborar en el estudio.\n(3) El término ''attend'' significa asistir a las clases o conferencias, lo cual es crucial para el aprendizaje.\n(4) ''Seek'' se utiliza aquí para indicar que los estudiantes buscan o buscan recursos adicionales para mejorar su educación.\n(5) ''Discuss'' implica hablar sobre los resultados del examen con otros, lo cual es una parte importante del proceso de aprendizaje.","complexity":2,"concept_tags":["vocabulary","education","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

