VALUES ('B1_U44_B1_I4', 'ingles-b1-U44', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''leaving'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''leaving'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En el discurso indirecto, usamos el tiempo pasado.","stimulus_en":"They told me, ''We are leaving now.''\n\nThey told me that they were... ...","correct_answer":"leaving now.","example":"leaving","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
