INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U44_B1_I5', 'ingles-b1-U44', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''help'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''help'':","mastery_tag":"grammar:b1-reported-speech","explanation":"Las preguntas indirectas cambian la estructura.","stimulus_en":"She said, ''Can you help me?''\n\nShe asked if I could... ...","correct_answer":"help her.","example":"help","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
