INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U49_B2_I2', 'ingles-b1-U49', 3, 'multiple_choice', 'Choose the correct option to complete each sentence in the past continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence in the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa ''were'' porque ''they'' es plural en pasado continuo.","stimulus_en":"They ___ (discuss) the project when I arrived.","options":[{"id":"o1","text":"were discussing"},{"id":"o2","text":"was discussing"},{"id":"o3","text":"discuss"},{"id":"o4","text":"discussed"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
