VALUES ('B1_U49_B2_I4', 'ingles-b1-U49', 5, 'multiple_choice', 'What ___ (you/do) while I was in the meeting?', '{"prompt_es":"What ___ (you/do) while I was in the meeting?","mastery_tag":"grammar:b1-past-continuous","explanation":"La estructura ''were you doing'' es correcta para preguntar en pasado continuo.","stimulus_en":"","options":[{"id":"o1","text":"were you doing"},{"id":"o2","text":"was you doing"},{"id":"o3","text":"are you doing"},{"id":"o4","text":"do you do"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
