INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U49_B2_I5', 'ingles-b1-U49', 6, 'multiple_choice', 'Choose the correct option to complete each sentence in the past continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence in the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa ''was'' porque la acción estaba ocurriendo en ese momento.","stimulus_en":"She ___ (read) a report when I called her.","options":[{"id":"o1","text":"was reading"},{"id":"o2","text":"were reading"},{"id":"o3","text":"is reading"},{"id":"o4","text":"read"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
