INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U49_B3_I2', 'ingles-b1-U49', 8, 'fill_blanks', 'Complete the sentences with the correct form of the verbs in brackets.', '{"prompt_es":"Complete the sentences with the correct form of the verbs in brackets.","mastery_tag":"grammar:b1-conditionals","explanation":"En este caso, usamos el segundo condicional porque estamos hablando de una situación hipotética.","stimulus_en":"Complete the sentences using the first or second conditional.\n\nIf I (2)___ (have) a million dollars, I would travel the world.","correct_answer":"had","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
