INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U49_B3_I3', 'ingles-b1-U49', 9, 'fill_blanks', 'Complete the sentences with the correct form of the verbs in brackets.', '{"prompt_es":"Complete the sentences with the correct form of the verbs in brackets.","mastery_tag":"grammar:b1-conditionals","explanation":"El primer condicional se utiliza aquí porque es una consecuencia real de no estudiar.","stimulus_en":"Complete the sentences using the first or second conditional.\n\nIf she (3)___ (not study), she will fail the exam.","correct_answer":"doesn''t study","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
