INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U49_B3_I4', 'ingles-b1-U49', 10, 'fill_blanks', 'Complete the sentences with the correct form of the verbs in brackets.', '{"prompt_es":"Complete the sentences with the correct form of the verbs in brackets.","mastery_tag":"grammar:b1-conditionals","explanation":"En este caso, el segundo condicional indica una situación irreal o improbable en el presente.","stimulus_en":"Complete the sentences using the first or second conditional.\n\nIf you (4)___ (be) more organized, you would finish your projects on time.","correct_answer":"were","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
