INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U49_B3_I5', 'ingles-b1-U49', 11, 'fill_blanks', 'Complete the sentences with the correct form of the verbs in brackets.', '{"prompt_es":"Complete the sentences with the correct form of the verbs in brackets.","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí se usa el primer condicional porque se refiere a una posible situación futura.","stimulus_en":"Complete the sentences using the first or second conditional.\n\nIf they (5)___ (invite) me to the party, I will definitely go.","correct_answer":"invite","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
