INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B1_I1', 'ingles-b1-U51', 1, 'fill_blanks', 'Complete the sentences with the correct words.', '{"prompt_es":"Complete the sentences with the correct words.","mastery_tag":"listening:b1-interviews","main_text":"During a job interview, it is important to make a good ___ (1). You should also prepare answers for common ___ (2). Employers often ask about your strengths and ___ (3). It is good to have questions ready to ask the ___ (4) at the end. Remember, a job interview is a chance for both sides to see if it is a good ___ (5).","correct_answer":["impression","questions","weaknesses","interviewer","fit"],"explanation":"(1) Es importante causar una buena impresión en una entrevista de trabajo para ser considerado para el puesto.\n(2) Preparar respuestas para preguntas comunes te ayudará a sentirte más seguro durante la entrevista.\n(3) Los empleadores suelen preguntar sobre tus fortalezas y debilidades para entender mejor tus habilidades.\n(4) Tener preguntas preparadas para el entrevistador muestra interés y motivación.\n(5) Una entrevista de trabajo es una oportunidad para evaluar si ambas partes son adecuadas para trabajar juntas.","complexity":2,"concept_tags":["listening","interviews","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B2_I1', 'ingles-b1-U51', 2, 'multiple_choice', 'What is the process of turning waste into reusable material called?', '{"prompt_es":"What is the process of turning waste into reusable material called?","mastery_tag":"vocabulary:b1-environment","explanation":"La respuesta correcta es ''Recycling'' porque es el proceso de convertir residuos en materiales reutilizables.","stimulus_en":"","options":[{"id":"o1","text":"Recycling"},{"id":"o2","text":"Pollution"},{"id":"o3","text":"Deforestation"},{"id":"o4","text":"Waste"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B2_I2', 'ingles-b1-U51', 3, 'multiple_choice', 'What is a major cause of climate change?', '{"prompt_es":"What is a major cause of climate change?","mastery_tag":"vocabulary:b1-environment","explanation":"Los ''Greenhouse gases'' son responsables del calentamiento global y del cambio climático.","stimulus_en":"","options":[{"id":"o1","text":"Ocean waves"},{"id":"o2","text":"Greenhouse gases"},{"id":"o3","text":"Trees"},{"id":"o4","text":"Water"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B2_I3', 'ingles-b1-U51', 4, 'multiple_choice', 'Which of the following is a common type of pollution?', '{"prompt_es":"Which of the following is a common type of pollution?","mastery_tag":"vocabulary:b1-environment","explanation":"La ''Air pollution'' ocurre cuando contaminantes dañan la calidad del aire.","stimulus_en":"","options":[{"id":"o1","text":"Solar"},{"id":"o2","text":"Air"},{"id":"o3","text":"Wind"},{"id":"o4","text":"Water"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B2_I4', 'ingles-b1-U51', 5, 'multiple_choice', 'What can individuals do to help the environment?', '{"prompt_es":"What can individuals do to help the environment?","mastery_tag":"vocabulary:b1-environment","explanation":"Reciclar ayuda a reducir residuos, salvar recursos y proteger el medio ambiente.","stimulus_en":"","options":[{"id":"o1","text":"Litter"},{"id":"o2","text":"Recycle"},{"id":"o3","text":"Waste food"},{"id":"o4","text":"Drive more"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
