INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B2_I5', 'ingles-b1-U51', 6, 'multiple_choice', 'What is the term for forests that are cut down?', '{"prompt_es":"What is the term for forests that are cut down?","mastery_tag":"vocabulary:b1-environment","explanation":"La ''Deforestation'' se refiere a la eliminación de árboles en un área de bosque.","stimulus_en":"","options":[{"id":"o1","text":"Deforestation"},{"id":"o2","text":"Reforestation"},{"id":"o3","text":"Afforestation"},{"id":"o4","text":"Forestation"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B3_I1', 'ingles-b1-U51', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''for'' para indicar la duración desde un momento específico en el pasado.","stimulus_en":"She started studying English in 2018.  (for)\n\nShe has been studying English for ...","correct_answer":"five years.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B3_I2', 'ingles-b1-U51', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''since'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''since'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Utilizamos ''since'' para referirnos al punto de inicio de una acción que todavía está en curso.","stimulus_en":"They began to work on the project last month.  (since)\n\nThey have been working on the project since ...","correct_answer":"last month.","example":"since","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B3_I3', 'ingles-b1-U51', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Cuando hablamos de una acción que ocurre regularmente, usamos ''for'' para indicar la duración.","stimulus_en":"He plays guitar every day.  (for)\n\nHe has been playing guitar for ...","correct_answer":"two hours.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B3_I4', 'ingles-b1-U51', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''since'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''since'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"Usamos ''since'' para marcar el inicio de un hábito que sigue presente.","stimulus_en":"We started to exercise regularly a year ago.  (since)\n\nWe have been exercising regularly since ...","correct_answer":"last year.","example":"since","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U51_B3_I5', 'ingles-b1-U51', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''for'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''for'':","mastery_tag":"grammar:b1-present-perfect-continuous","explanation":"El uso de ''for'' muestra la duración de una acción habitual desde el pasado hasta ahora.","stimulus_en":"She runs every morning.  (for)\n\nShe has been running every morning for ...","correct_answer":"six months.","example":"for","complexity":2,"concept_tags":["grammar","present","perfect","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

