INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B1_I1', 'ingles-b1-U53', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''PASS'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''PASS'':","mastery_tag":"grammar:b1-conditionals","explanation":"En esta oración, estamos hablando de una situación real que puede suceder si se cumple la condición de estudiar mucho.","stimulus_en":"If you study hard, you will pass the exam.\n\nYou will pass the exam if... ...","correct_answer":"you study hard.","example":"PASS","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B1_I2', 'ingles-b1-U53', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''GO'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''GO'':","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí se presenta una situación hipotética o irreal, ya que la invitación no ha ocurrido.","stimulus_en":"If they invited me, I would go to the party.\n\nI would go to the party if... ...","correct_answer":"they invited me.","example":"GO","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B1_I3', 'ingles-b1-U53', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''SUCCEED'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''SUCCEED'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta oración indica que el éxito es condicional a un esfuerzo mayor.","stimulus_en":"You won’t succeed if you don’t work harder.\n\nIf you don’t work harder, you won’t succeed. ...","correct_answer":"","example":"SUCCEED","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B1_I4', 'ingles-b1-U53', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''LEARN'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''LEARN'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta oración presenta una situación hipotética que depende de la condición mencionada.","stimulus_en":"If she had more time, she would learn a new language.\n\nShe would learn a new language if... ...","correct_answer":"she had more time.","example":"LEARN","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B1_I5', 'ingles-b1-U53', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''CANCEL'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''CANCEL'':","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí, la cancelación de la reunión depende de la condición de que llueva.","stimulus_en":"If it rains tomorrow, we will cancel the meeting.\n\nWe will cancel the meeting if... ...","correct_answer":"it rains tomorrow.","example":"CANCEL","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B2_I1', 'ingles-b1-U53', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''tell'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''tell'':","mastery_tag":"grammar:b1-reported-speech","explanation":"En el estilo indirecto, cambiamos el tiempo verbal y agregamos ''that''.","stimulus_en":"She said, ''I am happy.''\n\nShe told me... ...","correct_answer":"that she was happy.","example":"tell","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B2_I2', 'ingles-b1-U53', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''want'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''want'':","mastery_tag":"grammar:b1-reported-speech","explanation":"Utilizamos ''if'' para las preguntas de sí/no en el estilo indirecto.","stimulus_en":"He asked, ''Are you coming to the party?''\n\nHe wanted to know... ...","correct_answer":"if I was coming to the party.","example":"want","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B2_I3', 'ingles-b1-U53', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''mention'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''mention'':","mastery_tag":"grammar:b1-reported-speech","explanation":"El verbo ''will'' cambia a ''would'' en el tipo indirecto.","stimulus_en":"They said, ''We will leave early.''\n\nThey mentioned that... ...","correct_answer":"they would leave early.","example":"mention","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B2_I4', 'ingles-b1-U53', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''ask'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''ask'':","mastery_tag":"grammar:b1-reported-speech","explanation":"Cambiamos el tiempo y usamos ''if'' en el discurso indirecto.","stimulus_en":"She told me, ''I need help.''\n\nShe asked me... ...","correct_answer":"if she needed help.","example":"ask","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B2_I5', 'ingles-b1-U53', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''report'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''report'':","mastery_tag":"grammar:b1-reported-speech","explanation":"Usamos la forma negativa ''couldn''t'' en el discurso indirecto.","stimulus_en":"He said, ''I can''t find my keys.''\n\nHe reported that... ...","correct_answer":"he couldn''t find his keys.","example":"report","complexity":2,"concept_tags":["grammar","reported","speech","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U53_B3_I1', 'ingles-b1-U53', 11, 'multiple_choice', 'Choose the correct option to complete each sentence in passive voice.', '{"prompt_es":"Choose the correct option to complete each sentence in passive voice.","mastery_tag":"grammar:b1-passive","explanation":"En esta oración, el verbo debe estar en pasado y en voz pasiva, usando ''was'' + participio pasado.","stimulus_en":"The book ___ by the famous author.","options":[{"id":"o1","text":"was written"},{"id":"o2","text":"wrote"},{"id":"o3","text":"written"},{"id":"o4","text":"is write"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","passive","unit"]}')
