VALUES ('B1_U54_B2_I3', 'ingles-b1-U54', 8, 'multiple_choice', 'Choose the correct answer to complete the sentences about relative clauses.', '{"prompt_es":"Choose the correct answer to complete the sentences about relative clauses.","mastery_tag":"grammar:b1-relative-clauses","explanation":"''Which'' se usa aquí para añadir una información no definitoria sobre París.","stimulus_en":"The conference will be held in Paris, ___ is very beautiful.","options":[{"id":"o1","text":"who"},{"id":"o2","text":"that"},{"id":"o3","text":"where"},{"id":"o4","text":"when"}],"correct_answer":"which","complexity":2,"concept_tags":["grammar","relative","clauses","unit"]}')
