VALUES ('B1_U60_B2_I2', 'ingles-b1-U60', 3, 'multiple_choice', 'Choose the correct answer to complete the sentences using the passive voice.', '{"prompt_es":"Choose the correct answer to complete the sentences using the passive voice.","mastery_tag":"grammar:b1-passive","explanation":"Esta oración se refiere a una acción completada en el pasado, así que usamos ''was written''.","stimulus_en":"The book ___ by a famous author last year.","options":[{"id":"o1","text":"is written"},{"id":"o2","text":"was written"},{"id":"o3","text":"writes"},{"id":"o4","text":"wrote"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","passive","unit"]}')
