INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B1_I1', 'ingles-b1-U1', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''stay'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''stay'':","mastery_tag":"grammar:b1-conditionals","explanation":"En esta oración se usa el condicional de primera clase, donde ''if'' introduce la condición.","stimulus_en":"If it rains, I will stay home.\n\nI will stay home if it... ...","correct_answer":"rains.","example":"stay","complexity":2,"concept_tags":["grammar","conditionals","lifestyles","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B1_I2', 'ingles-b1-U1', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''drive'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''drive'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta es una oración condicional de segunda clase que habla de una situación hipotética.","stimulus_en":"If I had a car, I would drive to work.\n\nI would drive to work if I... ...","correct_answer":"had a car.","example":"drive","complexity":2,"concept_tags":["grammar","conditionals","lifestyles","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B1_I3', 'ingles-b1-U1', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''pass'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''pass'':","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí se utiliza el primer condicional, que se refiere a una situación real.","stimulus_en":"If you study hard, you will pass the exam.\n\nYou will pass the exam if you... ...","correct_answer":"study hard.","example":"pass","complexity":2,"concept_tags":["grammar","conditionals","lifestyles","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B1_I4', 'ingles-b1-U1', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''choose'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''choose'':","mastery_tag":"grammar:b1-conditionals","explanation":"Esta es una estructura de segundo condicional que habla de una situación irreal presente.","stimulus_en":"If I were you, I would choose that option.\n\nI would choose that option if I... ...","correct_answer":"were you.","example":"choose","complexity":2,"concept_tags":["grammar","conditionals","lifestyles","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B1_I5', 'ingles-b1-U1', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''answer'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''answer'':","mastery_tag":"grammar:b1-conditionals","explanation":"Este es otro ejemplo del primer condicional, donde la condición es real y futura.","stimulus_en":"If he calls, I will answer.\n\nI will answer if he... ...","correct_answer":"calls.","example":"answer","complexity":2,"concept_tags":["grammar","conditionals","lifestyles","routines"]}')
