INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B2_I1', 'ingles-b1-U1', 6, 'multiple_choice', 'Choose the correct option to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"La forma correcta es ''was watching'' porque indica una acción que estaba en progreso en el pasado.","stimulus_en":"While I ___ (watch) TV, my sister was reading a book.","options":[{"id":"o1","text":"was watch"},{"id":"o2","text":"was watching"},{"id":"o3","text":"watched"},{"id":"o4","text":"is watching"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","past","continuous","lifestyles","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B2_I2', 'ingles-b1-U1', 7, 'multiple_choice', 'Choose the correct option to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"La respuesta correcta es ''were playing'' porque describe una acción en progreso que fue interrumpida.","stimulus_en":"They ___ (play) soccer when it started to rain.","options":[{"id":"o1","text":"were playing"},{"id":"o2","text":"was playing"},{"id":"o3","text":"played"},{"id":"o4","text":"are playing"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","lifestyles","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B2_I3', 'ingles-b1-U1', 8, 'multiple_choice', 'Choose the correct option to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"La opción correcta es ''was walking'', indicando que la acción de caminar estaba ocurriendo en ese momento.","stimulus_en":"I saw her while she ___ (walk) in the park.","options":[{"id":"o1","text":"was walking"},{"id":"o2","text":"walking"},{"id":"o3","text":"has walked"},{"id":"o4","text":"is walking"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","lifestyles","routines"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B2_I4', 'ingles-b1-U1', 9, 'multiple_choice', 'Choose the correct option to complete each sentence using the past continuous tense.', '{"prompt_es":"Choose the correct option to complete each sentence using the past continuous tense.","mastery_tag":"grammar:b1-past-continuous","explanation":"La respuesta correcta es ''was not listening'' para expresar que no estaba prestando atención en ese momento.","stimulus_en":"He ___ (not listen) to the teacher when she asked him a question.","options":[{"id":"o1","text":"was not listening"},{"id":"o2","text":"did not listen"},{"id":"o3","text":"was not listen"},{"id":"o4","text":"not listening"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","lifestyles","routines"]}')
