INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B2_I5', 'ingles-b1-U1', 10, 'multiple_choice', 'What ___ (you/do) when I called you last night?', '{"prompt_es":"What ___ (you/do) when I called you last night?","mastery_tag":"grammar:b1-past-continuous","explanation":"La forma correcta es ''were you doing'', ya que se pregunta por una acción en progreso en el pasado.","stimulus_en":"","options":[{"id":"o1","text":"were you doing"},{"id":"o2","text":"did you do"},{"id":"o3","text":"was you doing"},{"id":"o4","text":"are you doing"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","past","continuous","lifestyles","routines"]}')
