VALUES ('B1_U1_B3_I1', 'ingles-b1-U1', 11, 'multiple_choice', 'What is one of the main causes of climate change?', '{"prompt_es":"What is one of the main causes of climate change?","mastery_tag":"vocabulary:b1-environment","explanation":"La deforestación libera grandes cantidades de CO2 en la atmósfera, contribuyendo al cambio climático.","stimulus_en":"","options":[{"id":"o1","text":"Deforestation"},{"id":"o2","text":"Recycling"},{"id":"o3","text":"Planting trees"},{"id":"o4","text":"Saving water"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","lifestyles","routines","grammar"]}')
