INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B3_I2', 'ingles-b1-U1', 12, 'multiple_choice', 'Which of the following is a renewable source of energy?', '{"prompt_es":"Which of the following is a renewable source of energy?","mastery_tag":"vocabulary:b1-environment","explanation":"La energía eólica proviene del viento, que es una fuente de energía renovable y no se agota.","stimulus_en":"","options":[{"id":"o1","text":"Coal"},{"id":"o2","text":"Natural gas"},{"id":"o3","text":"Wind"},{"id":"o4","text":"Oil"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","environment","lifestyles","routines","grammar"]}')
