INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B3_I3', 'ingles-b1-U1', 13, 'multiple_choice', 'What can individuals do to reduce pollution?', '{"prompt_es":"What can individuals do to reduce pollution?","mastery_tag":"vocabulary:b1-environment","explanation":"Reciclar reduce la cantidad de desechos y ayuda a mantener el medio ambiente limpio.","stimulus_en":"","options":[{"id":"o1","text":"Drive more"},{"id":"o2","text":"Use plastic bags"},{"id":"o3","text":"Recycle waste"},{"id":"o4","text":"Throw trash on the ground"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","environment","lifestyles","routines","grammar"]}')
