INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B3_I4', 'ingles-b1-U1', 14, 'multiple_choice', 'Which is an effect of global warming?', '{"prompt_es":"Which is an effect of global warming?","mastery_tag":"vocabulary:b1-environment","explanation":"El calentamiento global causa cambios en el clima, resultando en patrones meteorológicos más severos.","stimulus_en":"","options":[{"id":"o1","text":"Lower sea levels"},{"id":"o2","text":"More severe weather patterns"},{"id":"o3","text":"Less biodiversity"},{"id":"o4","text":"All of the above"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","environment","lifestyles","routines","grammar"]}')
