INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U1_B3_I5', 'ingles-b1-U1', 15, 'multiple_choice', 'What does biodiversity mean?', '{"prompt_es":"What does biodiversity mean?","mastery_tag":"vocabulary:b1-environment","explanation":"La biodiversidad se refiere a la variedad de especies en un ecosistema, lo cual es crucial para su salud.","stimulus_en":"","options":[{"id":"o1","text":"Variety of species"},{"id":"o2","text":"The number of trees"},{"id":"o3","text":"Amount of water"},{"id":"o4","text":"Extent of pollution"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","lifestyles","routines","grammar"]}')
