INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U5_B1_I1', 'ingles-b1-U5', 1, 'multiple_choice', 'Choose the correct answer for each question about conditional sentences.', '{"prompt_es":"Choose the correct answer for each question about conditional sentences.","mastery_tag":"grammar:b1-conditionals","explanation":"En esta oración se presenta una situación real. Si llueve, es posible que cancelen el picnic.","stimulus_en":"If it rains tomorrow, we ___ cancel the picnic.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"could"},{"id":"o4","text":"should"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
