INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U5_B1_I2', 'ingles-b1-U5', 2, 'multiple_choice', 'Choose the correct answer for each question about conditional sentences.', '{"prompt_es":"Choose the correct answer for each question about conditional sentences.","mastery_tag":"grammar:b1-conditionals","explanation":"Esta es una situación hipotética. Ganar la lotería es poco probable, por lo que usamos ''would''.","stimulus_en":"If I won the lottery, I ___ travel the world.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"might"},{"id":"o4","text":"could"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U5_B1_I3', 'ingles-b1-U5', 3, 'multiple_choice', 'Choose the correct answer for each question about conditional sentences.', '{"prompt_es":"Choose the correct answer for each question about conditional sentences.","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí, ''will'' indica un resultado positivo en base a una acción real.","stimulus_en":"If she studies hard, she ___ pass the exam.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"should"},{"id":"o4","text":"might"}],"correct_answer":"o1","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
