INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U5_B1_I4', 'ingles-b1-U5', 4, 'multiple_choice', 'Choose the correct answer for each question about conditional sentences.', '{"prompt_es":"Choose the correct answer for each question about conditional sentences.","mastery_tag":"grammar:b1-conditionals","explanation":"Esta es una situación hipotética. Usamos ''would'' para dar un consejo sobre algo que no es real.","stimulus_en":"If I were you, I ___ take that job.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"could"},{"id":"o4","text":"should"}],"correct_answer":"o2","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
