INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U5_B1_I5', 'ingles-b1-U5', 5, 'multiple_choice', 'Choose the correct answer for each question about conditional sentences.', '{"prompt_es":"Choose the correct answer for each question about conditional sentences.","mastery_tag":"grammar:b1-conditionals","explanation":"Aquí ''might'' expresa una posibilidad dependiendo de una condición real futura.","stimulus_en":"We ___ go hiking if the weather is nice this weekend.","options":[{"id":"o1","text":"will"},{"id":"o2","text":"would"},{"id":"o3","text":"might"},{"id":"o4","text":"should"}],"correct_answer":"o3","complexity":2,"concept_tags":["grammar","conditionals","unit"]}')
