INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B1_I1', 'ingles-b1-U46', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WAS'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WAS'':","mastery_tag":"grammar:b1-past-continuous","explanation":"En esta oración, usamos ''was'' para formar el pasado continuo junto con el verbo en -ing.","stimulus_en":"I (read) a book when the phone rang.\n\nI was reading ...","correct_answer":"a book when the phone rang.","example":"WAS","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B1_I2', 'ingles-b1-U46', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WERE'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WERE'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Aquí utilizamos ''were'' para formar el pasado continuo con un sujeto plural.","stimulus_en":"They (play) soccer when it started to rain.\n\nThey were playing ...","correct_answer":"soccer when it started to rain.","example":"WERE","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B1_I3', 'ingles-b1-U46', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''WAS'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''WAS'':","mastery_tag":"grammar:b1-past-continuous","explanation":"Usamos ''was'' para indicar que ella estaba en medio de la acción de cocinar.","stimulus_en":"She (cook) dinner when I arrived.\n\nShe was cooking ...","correct_answer":"dinner when I arrived.","example":"WAS","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
