INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B2_I1', 'ingles-b1-U46', 6, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"Se usa el past continuous para mostrar una acción en progreso en el pasado.","stimulus_en":"Fill in each blank with the correct form of the verb in the past continuous.\n\nWhile I ___ (read) the book, my friend called me.","correct_answer":"was reading","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B2_I2', 'ingles-b1-U46', 7, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"El past continuous se utiliza para indicar una acción que estaba en progreso en un momento específico.","stimulus_en":"Fill in each blank with the correct form of the verb in the past continuous.\n\nThey ___ (watch) a movie when the power went out.","correct_answer":"were watching","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B2_I3', 'ingles-b1-U46', 8, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"El uso negativo del past continuous se forma con ''was not'' o ''were not'' seguido del verbo en -ing.","stimulus_en":"Fill in each blank with the correct form of the verb in the past continuous.\n\nI ___ (not/sleep) when the alarm rang.","correct_answer":"was not sleeping","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B2_I4', 'ingles-b1-U46', 9, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"El past continuous se usa para describir acciones que estaban en curso mientras ocurría otra acción.","stimulus_en":"Fill in each blank with the correct form of the verb in the past continuous.\n\nWhile she ___ (cook), her friends arrived.","correct_answer":"was cooking","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B2_I5', 'ingles-b1-U46', 10, 'fill_blanks', 'Complete the sentences using the past continuous form of the verbs in parentheses.', '{"prompt_es":"Complete the sentences using the past continuous form of the verbs in parentheses.","mastery_tag":"grammar:b1-past-continuous","explanation":"Usamos el past continuous para mostrar que una acción estaba en progreso cuando ocurrió otra.","stimulus_en":"Fill in each blank with the correct form of the verb in the past continuous.\n\nWe ___ (play) football when it started to rain.","correct_answer":"were playing","complexity":2,"concept_tags":["grammar","past","continuous","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U46_B3_I1', 'ingles-b1-U46', 11, 'fill_blanks', 'Complete the sentences by filling in the blanks with the correct passive voice form.', '{"prompt_es":"Complete the sentences by filling in the blanks with the correct passive voice form.","mastery_tag":"grammar:b1-passive","main_text":"The book ___ (write) by a famous author.\n\nThe song ___ (sing) at the concert last night.\n\nThe painting ___ (sell) to a collector for a large amount.\n\nThe project ___ (complete) on time by the team.\n\nThe letters ___ (send) yesterday.","correct_answer":["was written","was sung","was sold","was completed","were sent"],"explanation":"(1) En voz pasiva, utilizamos ''was'' + participio pasado. Aquí, ''write'' se convierte en ''written''.\n(2) Usamos ''was'' porque la acción ocurrió en el pasado y ''sing'' se convierte en ''sung'' en participio pasado.\n(3) El participio pasado de ''sell'' es ''sold''. Usamos ''was'' para indicar que fue vendido en el pasado.\n(4) Para formar la voz pasiva, usamos ''was'' + participio pasado. ''Complete'' se convierte en ''completed''.\n(5) Aquí usamos ''were'' porque hay varias cartas y el participio pasado de ''send'' es ''sent''.","complexity":2,"concept_tags":["grammar","passive","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

