INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U48_B1_I2', 'ingles-b1-U48', 2, 'multiple_choice', 'Which process helps reduce waste by turning materials into reusable products?', '{"prompt_es":"Which process helps reduce waste by turning materials into reusable products?","mastery_tag":"vocabulary:b1-environment","explanation":"El reciclaje ayuda a reducir residuos al convertir materiales usados en nuevos productos.","stimulus_en":"","options":[{"id":"o1","text":"Composting"},{"id":"o2","text":"Recycling"},{"id":"o3","text":"Planting"},{"id":"o4","text":"Incinerating"}],"correct_answer":"o2","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
