INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U48_B1_I3', 'ingles-b1-U48', 3, 'multiple_choice', 'What is one effect of deforestation on the environment?', '{"prompt_es":"What is one effect of deforestation on the environment?","mastery_tag":"vocabulary:b1-environment","explanation":"La deforestación resulta en la pérdida de hábitats para muchas especies, afectando la biodiversidad.","stimulus_en":"","options":[{"id":"o1","text":"More biodiversity"},{"id":"o2","text":"Increased rainfall"},{"id":"o3","text":"Loss of habitats"},{"id":"o4","text":"Less pollution"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
