INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U48_B1_I4', 'ingles-b1-U48', 4, 'multiple_choice', 'What is a natural way to clean water?', '{"prompt_es":"What is a natural way to clean water?","mastery_tag":"vocabulary:b1-environment","explanation":"Filtrar el agua es un método natural para eliminar impurezas y hacerla potable.","stimulus_en":"","options":[{"id":"o1","text":"Filtering"},{"id":"o2","text":"Chlorinating"},{"id":"o3","text":"Boiling"},{"id":"o4","text":"Polluting"}],"correct_answer":"o1","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
