INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B1_U48_B1_I5', 'ingles-b1-U48', 5, 'multiple_choice', 'What do we call the melting of ice in polar regions due to global warming?', '{"prompt_es":"What do we call the melting of ice in polar regions due to global warming?","mastery_tag":"vocabulary:b1-environment","explanation":"El retroceso glacial se refiere al derretimiento de los glaciares debido al calentamiento global.","stimulus_en":"","options":[{"id":"o1","text":"Desertification"},{"id":"o2","text":"Ocean acidification"},{"id":"o3","text":"Glacial retreat"},{"id":"o4","text":"Urbanization"}],"correct_answer":"o3","complexity":2,"concept_tags":["vocabulary","environment","unit","grammar"]}')
